-- ----------------------------
-- Procedure structure for aggregate_procedure
-- ----------------------------
DROP PROCEDURE IF EXISTS `aggregate_procedure`;
delimiter ;;
CREATE PROCEDURE `aggregate_procedure`()
BEGIN
	TRUNCATE TABLE `aggregate`;
		image_path, category_name, full_date, day, month, year, GMT)
	SELECT nw.id, nw.title, nw.description, nw.content, nw.isdelete,
		iu.image_path, cd.category_name, dd.full_date, dd.`day`, dd.`month`, dd.`year`, dd.GMT
	FROM news_warehouse nw
		JOIN image_url_dim iu ON nw.image_url = iu.id
		JOIN category_dim cd ON nw.category = cd.id
		JOIN date_dim dd ON nw.date = dd.id
	WHERE NOT EXISTS (
		SELECT 1
		FROM `aggregate`
		WHERE `aggregate`.fact_id = nw.id
			AND `aggregate`.title = nw.title
			AND `aggregate`.description = nw.description
			AND `aggregate`.content = nw.content
			AND `aggregate`.isdelete = nw.isdelete
			AND `aggregate`.image_path = iu.image_path
			AND `aggregate`.category_name = cd.category_name
			AND `aggregate`.full_date = dd.full_date
			AND `aggregate`.day = dd.`day`
			AND `aggregate`.month = dd.`month`
			AND `aggregate`.year = dd.`year`
			AND `aggregate`.GMT = dd.GMT
	);
END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
